import './Users.css';
import PageTitle from "../../../../shared/PageTitle/PageTitle";
import ItemUser from "./ItemUser/ItemUser";
import {useDispatch, useSelector} from "react-redux";
import {
	HideLoadedUsers, SetCurrentPage, SetPageCount, SetPageSize, SetUsers,
	ToggleIsFetching, UpdateCurrentPageOnArrows
} from "../../../../redux/ActionCreators";
import axios from "axios";
import React, {useEffect} from 'react';
import LeftArrow from "../../../../shared/LeftArrow/LeftArrow";
import RightArrow from "../../../../shared/RightArrow/RightArrow";
import {UsersPaginationActions} from "../../../../redux/UsersPaginationActions";
import Preloader from "../../../../shared/Preloader/Preloader";


const Users = () => {
	let dispatch = useDispatch()
	let {users, page_size, page_count, current_page, isFetching}
		= useSelector(state => state.usersPage)

	const ajaxGET = (URL, callback) => {
		dispatch(ToggleIsFetching(true))
		axios.get(URL)
			.then(response => {
				if (callback) callback()
				dispatch(ToggleIsFetching(false))
				dispatch(SetUsers(response.data.results))
				dispatch(SetPageSize(response.data.results.length))
				dispatch(SetPageCount(response.data.count))
				return response
			}).catch(error => console.log(`FAIL ${error}`))
	}

	const updateCurrentPageOnArrows = pagination_action => {
		const URL = `http://192.168.100.8:8080/api/v1/profile?page=
		             ${current_page}`
		dispatch(HideLoadedUsers())
		ajaxGET(
			URL, () => dispatch(UpdateCurrentPageOnArrows(pagination_action))
		)
	}
	const updateCurrentPage = current_page => {
		const URL = `http://192.168.100.8:8080/api/v1/profile?page=
					 ${current_page}`
		dispatch(HideLoadedUsers())
		ajaxGET(URL, () => dispatch(SetCurrentPage(current_page)))
	}
	useEffect(() => {
		const BASE_URL = 'http://192.168.100.8:8080/api/v1/profile?page=1'
		ajaxGET(BASE_URL)
	}, [])

	const user_items = users.map(
		user => <ItemUser key={ user.id }
						  id={ user.id }
						  name={ user.username }
						  description={ user.description }
						  country={ user.country }
						  city={ user.city }/>)
	return <div className='users'>
			<PageTitle title='Users'/>
			<div className="users__body">
				{ isFetching && <Preloader/>}
				{ user_items }
			</div>
			<div className="pagination">
				<div className="pagination__container">
					<span className='pagination__left_arrow'
						  onClick={ previous }>
						<LeftArrow/>
					</span>
					{ page_numbers.map(page_number => {
						const classname = `pagination__item${
							current_page === page_number ? " selected" : ""
						}`
						return (
							<span className={ classname }
								  onClick={ () => updateCurrentPage(page_number) }>
								  { page_number }
							</span>)
					}) }
					<span className='pagination__right_arrow'
						  onClick={ next }>
						<RightArrow/>
					</span>
				</div>
			</div>
		</div>
}

export default Users;
