import './Posts.css';
import Post from "./Post/Post";
import React, {useContext} from "react";
import CreatePostButton
	from "./CreatePostButton/CreatePostButton";
import StoreContext from "../../../../store_context";

const Posts = (props) => {
	let latest_post_data = [].concat(props.state.posts_data).reverse()

	let posts_elements = latest_post_data.map(
		entry => <Post title={entry.title} content={entry.content}/>
	)
	console.log('STORE CONTEXT')
	console.log(useContext(StoreContext))


	return (
		<section className='posts'>
			<div className="posts__title">My posts</div>
			<div className='posts__create_button'>
				<CreatePostButton state={ props.state }
				                  dispatch={ props.dispatch }/>
			</div>
			<div className="posts__items">
				{ posts_elements }
			</div>

			<div className="general_profile__title">Profile</div>

		</section>
	)
}

export default Posts;
