const Action = {
	ADD_MESSAGE: "ADD-MESSAGE",
	UPDATE_MESSAGE: "UPDATE-MESSAGE",
}

let initial_state = {
	'posts_data': [
		{'id': 1, 'title': 'Post 1', 'content': 'content 1'},
		{'id': 2, 'title': 'Post 2', 'content': 'content 2'},
		{'id': 3, 'title': 'Post 3', 'content': 'content 3'},
		{'id': 4, 'title': 'Post 4', 'content': 'content 4'},
		{'id': 5, 'title': 'Post 5', 'content': 'content 5'},
		{'id': 6, 'title': 'Post 6', 'content': 'content 6'},
		{'id': 7, 'title': 'Post 7', 'content': 'content 7'},
	],
	'friends_cards_data': [
		{'id': 1, 'name': 'Vlad'},
		{'id': 1, 'name': 'Victor'},
		{'id': 1, 'name': 'Artur'},
		{'id': 1, 'name': 'Valera'},
		{'id': 1, 'name': 'Lera'},
	],
	'new_post_title': "",
	"new_post_content": ""
}

export const dialogs_reducer = (state = initial_state, action) => {
	switch (action.type) {
		case Action.ADD_MESSAGE:
			action.message_set.messages.push({
				'id': 3,
				'name': 'Me',
				'content': action.message_set.new_message,
			}); break
		case Action.UPDATE_MESSAGE:
			action.message_set.new_message = action.new_message
			break
		default: break
	}
	return state
}

export const add_message_action_creator = (message_set) => ({
	type: Action.ADD_MESSAGE,
	message_set: message_set
})
export const update_message_action_creator = (message_set, new_message) => ({
	type: Action.UPDATE_MESSAGE,
	message_set: message_set,
	new_message: new_message,
})

export default dialogs_reducer;
