import store from "./redux/state";
import ReactDOM from "react-dom";
import React from "react";
import {BrowserRouter} from "react-router-dom";
import App from "./App";
import reportWebVitals from "./reportWebVitals";

const root = ReactDOM.createRoot(document.getElementById('root'))

export const rerender_entire_tree = (state, add_post, update_post) => {
    root.render(
        <React.StrictMode>
            <BrowserRouter>
                <App state={state} add_post={add_post} update_post={update_post}/>
            </BrowserRouter>
        </React.StrictMode>
    )
}

rerender_entire_tree(store._state, store.add_post, store.update_post)

store.subscribe(rerender_entire_tree)

reportWebVitals()
