import './MessageCreateForm.css';
import React from 'react';

import {AddMessage, UpdateMessage} from "../../../../redux/ActionCreators";
import {useDispatch} from "react-redux";

const MessageCreateForm = (props) => {
	let dispatch = useDispatch()

	const add_message = () => {
		let action = AddMessage(props.message_set)
		dispatch(action)
	}
	const update_message = (elem) => {
		console.log('UPDATE MESSAGE')
		let new_message = elem.target.value
		let action = UpdateMessage(props.message_set, new_message)
		dispatch(action)
	}

	return (
		<div className="create_post">
			<p><textarea onChange={ update_message }
						 value={ props.message_set.new_message }>
			</textarea></p>

			<p><button onClick={ add_message }>Create post</button></p>
		</div>
	)
}

export default MessageCreateForm;
