import './Users.css';
import PageTitle from "../../../../shared/PageTitle/PageTitle";
import ItemUser from "./ItemUser/ItemUser";
import {useDispatch, useSelector} from "react-redux";
import {
	HideLoadedUsers,
	SetPageCount, SetPageSize, SetUsers, ToggleIsFetching,
} from "../../../../redux/ActionCreators";
import React, {useEffect} from 'react';
import Preloader from "../../../../shared/Preloader/Preloader";
import Pagination from "./Pagination/Pagination";
import {setUsers} from "../../../../redux/api";


const Users = () => {
	let dispatch = useDispatch()
	let { users, isFetching } = useSelector(state => state.usersPage)

	const ajaxGET = (URL) => {
		let { auth_token } = useSelector(state => state.auth)
		dispatch(HideLoadedUsers())
    	dispatch(ToggleIsFetching(true))
		setUsers(URL, auth_token)
			.then(response => {
				dispatch(ToggleIsFetching(false))
				dispatch(SetUsers(response.data.results))
				dispatch(SetPageSize(response.data.results.length))
				dispatch(SetPageCount(response.data.count))
				return response
			})
	}
	useEffect(() => {
		const URL = 'http://192.168.100.8:8080/api/v1/profile?page=1'
		ajaxGET(URL, auth_token)
	}, [])

	const user_items = users.map(
		user => <ItemUser key={ user.id }
						  id={ user.id }
						  name={ user.username }
						  description={ user.description }
						  country={ user.country }
						  city={ user.city }/>)

	return <div className='users'>
			<PageTitle title='Users'/>
			<div className="users__body">
				{ isFetching && <Preloader/>}
				{ user_items }
			</div>
			<Pagination ajaxGET={ ajaxGET } auth_token={ auth_token }/>
		</div>
}

export default Users;
