import './Dialogs.css';
import ItemMessage from "./ItemMessage/ItemMessage";
import ItemDialogs from "./ItemDialogs/ItemDialogs";
import {Route, Routes} from "react-router-dom";
import CreatePostButton from "../../../features/CreatePostButton/CreatePostButton";

const Dialogs = (props) => {
	let dialogs_elements = props.state.dialogs_data.map(
		entry => <ItemDialogs id={entry.id} name={entry.name}/>
	)
	return (
		<div className='dialogs'>
			<div className="dialogs__body">
				<ul className='dialogs__list'>
					{ dialogs_elements }

					<div className="general_dialogs__title">Dialogs</div>
				</ul>
				<div className="dialogs__current">
					<Routes>
						{ props.state.messages_data.map(object => (
							<Route path={`/${object.id}`} element={
								<div>
									<div>
										{ object.messages.map(message => {
												return (
													<ItemMessage
														name={message.name}
														content={message.content}
													/>
												)
										})}
									</div>
									<CreatePostButton />
								</div>
							}/>
						))}
					</Routes>
				</div>
			</div>
		</div>
	)
}

export default Dialogs;
