import './Dialogs.css';
import ItemMessage from "./ItemMessage/ItemMessage";
import ItemDialogs from "./ItemDialogs/ItemDialogs";
import {Route, Routes} from "react-router-dom";
import CreatePostButton from "./CreatePostButton/CreatePostButton";
import StoreContext from "../../../store_context";
import {useContext} from "react";

const Dialogs = () => {
	let {state} = useContext(StoreContext)

	let dialogs_elements = state.dialogs_data.map(
		entry => <ItemDialogs id={entry.id} name={entry.name}/>
	)
	return (
		<div className='dialogs'>
			<div className="dialogs__body">
				<ul className='dialogs__list'>
					{ dialogs_elements }

					<div className="general_dialogs_title">Dialogs</div>
				</ul>
				<div className="dialogs__current">
					<Routes>
						{ state.messages_data.map(message_set => (
							<Route path={`/${message_set.id}`} element={
								<div className='dialogs__container'>
									<div className='dialogs__messages'>
										{ message_set.messages.map(message => {
												return (
													<ItemMessage
														state={ message }
													/>
												)
										})}
									</div>
									<CreatePostButton message_set={ message_set }/>
								</div>
							}/>
						))}
					</Routes>
				</div>
			</div>
		</div>
	)
}

export default Dialogs;
