import './Users.css';
import PageTitle from "../../../../shared/PageTitle/PageTitle";
import ItemUser from "./ItemUser/ItemUser";
import {connect, useDispatch, useSelector} from "react-redux";
import ShowMoreButton from "./ShowMoreButton/ShowMoreButton";
import {SetUsers} from "../../../../redux/ActionCreators";
import axios from "axios";
import React from 'react';


class Users extends React.Component {
	constructor(props) {
		super(props)
	}

	setUsers = () => {
		axios.get(`http://192.168.100.8:8080/api/v1/profile`)
			.then(this.props.set_users)
			.catch(err => console.log(`FAIL ${err}`))
	}
	render() {
		const user_items = this.props.users.map(
			user => <ItemUser key={ user.id }
							  id={ user.id }
							  name={ user.username }
							  description={ user.description }
							  country={ user.country }
							  city={ user.city }/>)

		return <div className='users'>
				<PageTitle title='Users'/>
				<div className="users__body">{ user_items }</div>
				<ShowMoreButton setUsers={ this.setUsers }/>
			</div>
	}
	componentDidMount() {
		this.setUsers()
	}
}

const mapStateToProps = (state) => ({'users': state.usersPage.users})
const mapDispatchToProps = (dispatch) => ({
	set_users: response => {
		const action = SetUsers(response.data.results)
		dispatch(action)
	}
})

export default connect(mapStateToProps, mapDispatchToProps)(Users)

