import './CreatePostButton.css';
import React, {useContext} from 'react';
import {
	add_message_action_creator,
	update_message_action_creator
} from "../../../../redux/dialogs_reducer";
import StoreContext from "../../../../store_context";

const CreatePostButton = (props) => {
	let {dispatch} = useContext(StoreContext)

	let new_message_ref = React.createRef()

	const add_message = () => {
		let action = add_message_action_creator(props.message_set)
		dispatch(action)
	}
	const update_message = () => {
		let new_message = new_message_ref.current.value
		let action = update_message_action_creator(props.message_set, new_message)
		dispatch(action)
	}

	return (
		<div className="create_post">
			<p>
				<textarea onChange={ update_message } ref={ new_message_ref }
				          value={ props.message_set.new_message }></textarea>
			</p>
			<p>
				<button onClick={ add_message }>Create post</button>
			</p>
		</div>
	)
}

export default CreatePostButton;
