import './CreatePostButton.css';
import React from 'react';

const CreatePostButton = () => {

	let post_textarea = React.createRef()
	let add_post = () => {
		let post_data = post_textarea.current.value
		alert(post_data)
	}

	return (
		<div className="create_post">
			<p>
				<textarea ref={ post_textarea }></textarea>
			</p>
			<p>
				<button onClick={ add_post }>Create post</button>
			</p>
		</div>
	)
}

export default CreatePostButton;
