import './CreatePostButton.css';
import React from 'react';
import {
	add_post_action_creator,
	update_new_post_action_creator
} from "../../../../../redux/profile_reducer";

const CreatePostButton = (props) => {
	let post_title_ref = React.createRef()
	let post_content_ref = React.createRef()

	let update_post = () => {
		let post_title = post_title_ref.current.value
		let post_content = post_content_ref.current.value

		let action = update_new_post_action_creator(post_title, post_content)
		props.dispatch(action)
	}

	return (
		<div className="create_post">
			<p>
				<input onChange={ update_post } ref={ post_title_ref }
				       value={ props.state.new_post_title } type='text' />
			</p>
			<p>
				<textarea onChange={ props.update_post }
				          ref={ post_content_ref }
				          value={ props.state.new_post_content }>
				</textarea>
			</p>
			<p>
				<button onClick={ add_post }>Create post</button>
			</p>
		</div>
	)
}

export default CreatePostButton;
