import Action from "../Actions";

let initialState = {
	'users': [],
	'page_count': 0,
	'page_size': 0,
	'current_page': 1,
}

const usersReducer = (state = initialState, action) => {
	switch (action.type) {
		case Action.SET_USERS: {
			let stateCopy = structuredClone(state)
			stateCopy.users = action.users
			stateCopy.page_size = action.page_size
			stateCopy.page_count = action.page_count

			stateCopy.current_page = action.current_page

			const pages = state.page_count / state.page_size

			if (action.page_action === 'next' && stateCopy.current_page < pages)
				stateCopy.current_page++
			else if (action.page_action === 'previous' && stateCopy.current_page > 1) {
				stateCopy.current_page--
			}

			return stateCopy
		}
		default: return state
	}
}

export default usersReducer;
