const Action = {
	ADD_MESSAGE: "ADD-MESSAGE",
	UPDATE_MESSAGE: "UPDATE-MESSAGE",
}

export const dialogs_reducer = (state, action) => {
	switch (action.type) {
		case Action.ADD_MESSAGE:
			action.message_set.messages.push({
				'id': 3,
				'name': 'Me',
				'content': action.message_set.new_message,
			}); break
		case Action.UPDATE_MESSAGE:
			action.message_set.new_message = action.new_message
			break
		default: break
	}
	return state
}

export const add_message_action_creator = (message_set) => ({
	type: Action.ADD_MESSAGE,
	message_set: message_set
})
export const update_message_action_creator = (message_set, new_message) => ({
	type: Action.UPDATE_MESSAGE,
	message_set: message_set,
	new_message: new_message,
})

export default dialogs_reducer;
