import './Users.css';
import PageTitle from "../../../../shared/PageTitle/PageTitle";
import ItemUser from "./ItemUser/ItemUser";
import {connect, useDispatch, useSelector} from "react-redux";
import {SetUsers} from "../../../../redux/ActionCreators";
import axios from "axios";
import React from 'react';


class Users extends React.Component {
	render() {
		const props = this.props
		const user_items = props.users.map(
			user => <ItemUser key={ user.id }
							  id={ user.id }
							  name={ user.username }
							  description={ user.description }
							  country={ user.country }
							  city={ user.city }/>)
		const pages = props.page_count / props.page_size
		const page_numbers = pages ? [...Array(pages+1).keys()].splice(1) : []

		return <div className='users'>
				<PageTitle title='Users'/>
				<div className="users__body">{ user_items }</div>
				<div className="pagination">
					<div className='left_arrow'></div>
					{ page_numbers.map(page_number => {
						const classname = `pagination__item${
							props.current_page === page_number ? " selected" : ""
						}`
						const DidMount = () => this.componentDidMount(page_number)
						return <span className={ classname }
									 onClick={ DidMount }>
								 	{ page_number }
						       </span>
					}) }
					<div className='right_arrow'></div>
				</div>
			</div>
	}
	componentDidMount(page_number = 1) {
		axios.get(`http://192.168.100.8:8080/api/v1/profile?page=${ page_number }`)
			.then((response) => this.props.set_users(response, page_number))
			.catch(err => console.log(`FAIL ${err}`))
	}
}

const mapStateToProps = (state) => ({
	'users': state.usersPage.users,
	'page_size': state.usersPage.page_size,
	'page_count': state.usersPage.page_count,
	'current_page': state.usersPage.current_page,
})
const mapDispatchToProps = (dispatch) => ({
	set_users: (response, page_number) => {
		const results = response.data.results
		const page_size = results.length
		const page_count = response.data.count
		const action = SetUsers(results, page_size, page_count, page_number)
		dispatch(action)
	}
})

export default connect(mapStateToProps, mapDispatchToProps)(Users)

