import './Dialogs.css';
import ItemMessage from "./ItemMessage/ItemMessage";
import ItemDialogs from "./ItemDialogs/ItemDialogs";
import {Route, Routes} from "react-router-dom";
import CreateMessageForm from "./CreateMessageForm/CreateMessageForm";
import {useSelector} from "react-redux";
import PageTitle from "../../../../shared/PageTitle/PageTitle";

const Dialogs = () => {
	let dialogsPage = useSelector(state => state.dialogsPage)

	let dialogs_elements = dialogsPage.dialogs.map(
		entry => <ItemDialogs key={ entry.id } id={ entry.id }
							  name={ entry.name }/>)

	let messages_to_each_dialog = dialogsPage.messages_sets.map(
		(message_set) => (
			<Route path={`/${message_set.id}`} element={
				<div className='dialog'>
					<div className='dialog__messages'>
						{ message_set.messages.map(message => {
								return <ItemMessage key={ message.id }
										   message={ message }/>})}
					</div>
					<CreateMessageForm key={ message_set.id }
									   _message_set={ message_set }/>
				</div>
			}/>
		))
	return (
		<div className='dialogs'>
			<PageTitle title='Dialog'/>
			<div className="dialogs__body">
				<ul className='dialogs__list'>{ dialogs_elements }</ul>
				<div className="dialogs__current">
					<Routes>
						{ messages_to_each_dialog }
					</Routes>
				</div>
			</div>
		</div>
	)
}

export default Dialogs;
