import './CreateButton.css';
import React from 'react'

const CreateButton = () => {

	let new_post_element = React.createRef()

	let add_post = () => {
		let post_data = new_post_element.current.value

		alert( post_data )
	}

	return (
		<div className="create_post">
			<p>
				<textarea ref={ new_post_element } onResize={ () => false }></textarea>
			</p>
			<p>
				<button onClick={ add_post }>CREATE POST</button>
			</p>
		</div>
	)
}

export default CreateButton;
