import React, {useContext} from 'react';
import './SideBar.css';
import FriendCard from "./FriendCard/FriendCard";
import StoreContext from "../../../../store_context";

const SideBar = () => {
	let {state} = useContext(StoreContext)

	let friends_cards_elements = state.profile_reducer.friends_cards_data.map(
		elem => <FriendCard name={ elem.name } />
	)
	return (
		<aside className='sidebar'>
			<div className="sidebar__title">Friends</div>
			<div className="sidebar__body">
				{ friends_cards_elements }
			</div>
		</aside>
	)
}

export default SideBar;
