const Action = {
	ADD_POST: "ADD-POST",
	UPDATE_POST: "UPDATE-POST",
}

let initial_state = {
	'posts_data': [
		{'id': 1, 'title': 'Post 1', 'content': 'content 1'},
		{'id': 2, 'title': 'Post 2', 'content': 'content 2'},
		{'id': 3, 'title': 'Post 3', 'content': 'content 3'},
		{'id': 4, 'title': 'Post 4', 'content': 'content 4'},
		{'id': 5, 'title': 'Post 5', 'content': 'content 5'},
		{'id': 6, 'title': 'Post 6', 'content': 'content 6'},
		{'id': 7, 'title': 'Post 7', 'content': 'content 7'},
	],
	'friends_cards_data': [
		{'id': 1, 'name': 'Vlad'},
		{'id': 1, 'name': 'Victor'},
		{'id': 1, 'name': 'Artur'},
		{'id': 1, 'name': 'Valera'},
		{'id': 1, 'name': 'Lera'},
	],
	'new_post_title': "",
	"new_post_content": ""
}

const profile_reducer = (state = initial_state, action) => {
	switch (action.type) {
		case Action.ADD_POST:
			let new_post = {
				'id': 5,
				'title': state.new_post_title,
				'content': state.new_post_content,
			}
			state.posts_data.push(new_post)
			state.new_post_title = ""
			state.new_post_content = ""
			break
		case Action.UPDATE_POST:
			state.new_post_title = action.post_title
			state.new_post_content = action.post_content
			break
		default: break
	}
	return state
}

export const add_post_action_creator = () => ({'type': Action.ADD_POST})
export const update_new_post_action_creator = (post_title, post_content) => ({
	type: Action.UPDATE_POST,
	post_title: post_title,
	post_content: post_content,
})

export default profile_reducer;
