import './CreatePostButton.css';
import React from 'react';

const CreatePostButton = (props) => {
	let post_title_ref = React.createRef()
	let post_content_ref = React.createRef()

	let add_post = () => {
		let post_title = post_title_ref.current.value
		let post_content = post_content_ref.current.value
		props.add_post(post_title, post_content)
		post_title_ref.current.value = ""
		post_content_ref.current.value = ""
	}

	return (
		<div className="create_post">
			<p>
				<input onKeyDown={ add_post } ref={post_title_ref} type='text' />
			</p>
			<p>
				<textarea onKeyDown={ add_post } ref={post_content_ref}></textarea>
			</p>
			<p>
				<button onClick={ add_post }>Create post</button>
			</p>
		</div>
	)
}

export default CreatePostButton;
